      (error: microsoftTeams.SdkError, decodedText: string) => {
        if (error) {
          let errorMessage;
          switch (error.errorCode) {
            case 100:
              errorMessage = "平台不支持";
              break;
            case 500:
              errorMessage = "内部错误";
              break;
            case 1000:
              errorMessage = "权限不足，用户没有接受";
              break;
            case 3000:
              errorMessage = "硬件不支持";
              break;
            case 4000:
              errorMessage = "错误的参数";
              break;
            case 8000:
              errorMessage = "用户取消操作";
              break;
            case 8001:
              errorMessage = "用户操作超时";
              break;
            case 9000:
              errorMessage = "平台太老";
              break;
            default:
              errorMessage = "未知错误";
              break;
          }
          setMessage("发生错误:" + errorMessage);
        } else if (decodedText) {
          // 这里是指扫描到了有关的二维码，本例不做具体的检测。通常会是一个网址，然后这个网址跟具体某个柜子是有关系的，然后通过这个网址发送请求，完成后续的开箱的操作。
          // 本例，只要扫描到了，就访问本地存储去尝试进行登记。
          setMessage("扫码成功:" + decodedText);
          // 随机生成一个箱子编号，也可能没有箱子的情况
          let id = Math.floor(Math.random() * 30);
          let available = id % 2 === 0;
          if (available) {
            let items = historyItems;
            items?.push({
              BoxNumber: id,
            });

            localStorage.setItem("history", JSON.stringify(items));
            setSelectedBox(id);
            setState(4);
          } else {
            setState(3);
            setTimeout(() => {
              setState(1);
            }, 5000);
          }
        }
        //设置两秒后自动消息
        setTimeout(() => {
          setMessage("");
        }, 2000);
      },
      config
    );
  };

然后我修改了“确认预定” 和 “归还”的函数，分别如下
